import React,{useEffect,useState} from 'react'
import { Col, ListGroup,Image, Row,Card,Button, ListGroupItem ,Form} from 'react-bootstrap'
import {useNavigate,Link, useMatch, useParams } from 'react-router-dom'
import Rating from '../components/Rating' 
import Message from '../components/Message'
import { useDispatch,useSelector } from 'react-redux'
import { listProductDetails,createProductReview} from '../actions/productActions'

// import { useHistory } from "react-router-dom";
import Loader from '../components/Loader'
import axios from 'axios'
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants'
const Productscreens = ({}) => {
  const [qty,setqty]=useState(1);
  const[rating,setRating]=useState('')
  const[comment,setComment]=useState('')

  //  match = useMatch();
  const {id} = useParams();
  const navigate = useNavigate();
    // const [product,setprodu-ct]=useState({});
    // console.log(id)
    const dispatch=useDispatch()
    const pr=useSelector(state=>state.productDetails)
      const {loading,error,product}=pr

      const userLogin=useSelector(state=>state.userLogin)
      const {userInfo}=userLogin

      const productReviewCreate=useSelector(state=>state.productReviewCreate)
      const{success:successProductReview,error:errorProductReview}=productReviewCreate


      useEffect(()=>{

        if(successProductReview){
          alert('Review Submitted!')
          setRating(0)
          setComment('')
          dispatch({type:PRODUCT_CREATE_REVIEW_RESET})
        }
      dispatch(listProductDetails(id))


      // const fetchProducts=async ()=>{
        // const {data}=await axios.get(`/api/products/${match.id}`);
        // setproduct(data);
        // console.log(id._id)

      // }
      // fetchProducts()
// console.log('this is id',id)
     },[dispatch,id,successProductReview])
     const addToCartHandler =()=>{
        navigate(`/cart/${id}/${qty}`)

      }
      const submitHandler=(e)=>{
        e.preventDefault()
        dispatch(createProductReview(id,{rating,comment}))
      }

// product={}

  return ( 
    <>
        <Link className='btn btn-dark my-3' to="/">Go back</Link>
       {loading?<Loader/>:error?<Message variant='danger'> {error}</Message>:(
        <>
        <Row>
        <Col md={6}>
          {/* {product.image} */}
          <Image src ={product.image} alt={product.name} fluid/>
        </Col>
        <Col md={3}>
           <ListGroup variant='flush'>
               <ListGroup.Item>
                   <h2>{product.name}</h2>
               </ListGroup.Item>
               <ListGroup.Item>
                   <Rating value={product.rating} text={`${product.numReviews} reviews`}/>
               </ListGroup.Item>
               <ListGroup.Item>
                Price:${product.price}
               </ListGroup.Item>
               <ListGroup.Item>
                Description:${product.description}
               </ListGroup.Item>

           </ListGroup>

        </Col>
            <Col md={3}>
              <Card>
                <ListGroup variant='flush'>
                  <ListGroupItem>
                    <Row>
                      <Col>
                        Price:
                      </Col>
                      <Col>
                      <strong>${product.price}</strong>
                      </Col>
                    </Row>
                  </ListGroupItem>

                  <ListGroup.Item>
                    <Row>
                      <Col>
                        Status:
                      </Col>
                      <Col>
                      {product.countInStock>0?'In Stock':'Out of Stock'}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                  {product.countInStock > 0 && (
                      <ListGroup.Item>
                        <Row>
                          <Col>Qty</Col>
                          <Col>
                            <Form.Control as='select' value={qty} onChange={(e)=>
                            setqty(e.target.value)}>
                              {
                              [...Array(product.countInStock).keys()].map((x)=>(
                                <option key={x+1} value={x+1}>
                                  {x+1}
                                </option>
                              ))}
                            </Form.Control>
                          </Col>
                        </Row>
                      </ListGroup.Item>
                  )}



                  <ListGroup.Item>
                    <Button className='btn-block' type='button'
                      onClick={addToCartHandler}
                      disabled={product.countInStock===0}>
                      Add to Cart
                    </Button>
                  </ListGroup.Item>
                </ListGroup>
              </Card>
            </Col>
     </Row> 
    <Row>
      <Col md={6}>
       <h2>Reviews</h2>
       {product.reviews.length===0 &&<Message>No Reviews</Message>}
       <ListGroup variant='flush'>
              {product.reviews.map(review=>(
                <ListGroup.Item key={review._id}>
                    <strong>{review.name}</strong>
                    <Rating value={review.rating}/>
                    <p>{review.createdAt.substring(0,10)}</p>
                    <p>{review.comment}</p>
                </ListGroup.Item>
              ))}
              <h2>Write a Customer Review</h2>
              {errorProductReview && <Message variant='danger'>{errorProductReview}</Message>}
              {userInfo?(
              <Form onSubmit={submitHandler}>
                <Form.Group controlId='rating'>
                  <Form.Label>Rating</Form.Label>
                    <Form.Control as='select' value={rating} onChange={(e)=>
                      setRating(e.target.value)}>
                        <option value=''>Select...</option>
                        <option value='1'>1 - Poor</option>
                        <option value='2'>2 - Fair</option>
                        <option value='3'>3 - Good</option>
                        <option value='4'>4 - Very good</option>
                        <option value='5'>5 - Excellent</option>
                      </Form.Control>
                  </Form.Group>  
                <Form.Group controlId='comment'>
                  <Form.Label>Comment</Form.Label>
                  <Form.Control as='textarea' rows='3'
                    value={comment} onChange={(e)=>setComment(e.target.value)}>
                  </Form.Control>

                </Form.Group>
                <Button type='submit' variant='primary'>
                  Submit
                </Button>
              </Form>):(<Message>
              Please <Link to='/login' >sign in</Link>to write a review
              {' '}</Message>)}
       </ListGroup>
      </Col>
    </Row>

     </>
      )}

    </>

  )
}


export default Productscreens
