import React,{useState,useEffect} from 'react'
import {LinkContainer} from 'react-router-bootstrap'
import { ReactLocation, Router } from 'react-location'
import {useNavigate} from "react-router-dom"

import {Table,Button,Col,Row} from 'react-bootstrap'
import {useDispatch,useSelector} from 'react-redux'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { listProduct,deleteProduct,createProduct } from '../actions/productActions'
import { PRODUCT_CREATE_RESET } from '../constants/productConstants'

const ProductListScreen = () => {
    const dispatch=useDispatch()
    const navigate=useNavigate()
    const productList=useSelector(state=>state.productList)
    const{loading,error,products}=productList

    const productDelete=useSelector(state=>state.productDelete)
    const{loading:loadingDelete,error:errorDelete,success:successDelete}=productDelete

    const productCreate=useSelector(state=>state.productCreate)
    const{loading:loadingCreate,error:errorCreate,success:successCreate,product:createdProduct}=productCreate
    // console.log(createProduct)
    const userLogin=useSelector((state)=>state.userLogin)
    const {userInfo}=userLogin

    useEffect(()=>{
        dispatch({type:PRODUCT_CREATE_RESET})

        if(userInfo.name!=="Admin User"){
        // dispatch(listProduct())}
            navigate('/login')
    }
        if(successCreate){  dispatch(listProduct())
            navigate(`/admin/product/${createdProduct._id}/edit`)
        }else{
            dispatch(listProduct())
        }

    },[dispatch,navigate,userInfo,successDelete,successCreate,createdProduct])

    // console.log(users)

    const DeleteListHandler=(id)=>{
        if(window.confirm('Are you sure')){
        dispatch(deleteProduct(id))}
    }
    const createProductHandler=()=>{
        dispatch(createProduct())
    }
    return (
    <> 
    <Row className='align-items-center'>
        <Col>
            <h1>Products</h1>
        </Col>
        <Col className='text-right'>
            <Button className='my-3' onClick={createProductHandler}>
                <i className='fas fa-plus'></i>
            </Button>
        </Col>
    </Row>

        {/* {loadingDelete && <Loader/>} */}
        {errorDelete && <Message variant='danger'>{errorDelete}</Message>}

          {loadingCreate && <Loader/>} 
         {errorCreate && <Message variant='danger'>{errorCreate}</Message>}
    {loading?<Loader/>:error?<Message variant='danger'>{error}</Message>
    :(
        <Table striped bordered hover responsive className='table-sm'>
            <thead>
                <tr>
                    <th></th>
                    <th>ID</th>
                    <th>NAME</th>
                    <th>PRICE</th>
                    <th>CATEGORY</th>
                    <th>BRAND</th>
                    <th></th>
                </tr>
            </thead>
            <tbody>

                {products.map(product=>(
                    <tr key={product._id}>
                        <td>{product._id}</td>
                        <td>{product.name}</td>
                        <td>${product.price}</td>
                        <td>{product.category}</td>
                        <td>{product.brand}</td>
                        <td>

                            <LinkContainer to={`/admin/product/${product._id}/edit`}>
                                <Button variant='light' className='btn-sm'>
                                    <i className='fas fa-edit'></i>
                                </Button>
                            </LinkContainer>
                            <Button variant='danger' className='btn-sm' onClick={()=>DeleteListHandler(product._id)}>
                                <i className='fas fa-trash'></i>
                            </Button>
                        </td>
                    </tr>
                ))}
            </tbody>

        </Table>
    )}


    </>
  )
}

export default ProductListScreen
