import React,{useEffect,useState} from 'react'
import { Col, ListGroup,Image, Row,Card,Button, ListGroupItem ,Form} from 'react-bootstrap'
import {useNavigate,Link, useMatch, useParams } from 'react-router-dom'
import Rating from '../components/Rating' 
import Message from '../components/Message'
import { useDispatch,useSelector } from 'react-redux'
import { listProductDetails } from '../actions/productActions'

// import { useHistory } from "react-router-dom";
import Loader from '../components/Loader'
import axios from 'axios'
const Productscreens = ({match}) => {
  const [qty,setqty]=useState(0);
  //  match = useMatch();
  // const {id} = useParams();
  const navigate = useNavigate();
    // const [product,setprodu-ct]=useState({});
    // console.log(id)
    const dispatch=useDispatch()
    const pr=useSelector(state=>state.pr)
      const {loading,error,product}=pr
     useEffect(()=>{
      dispatch(listProductDetails(match.params.id))

      // const fetchProducts=async ()=>{
        // const {data}=await axios.get(`/api/products/${match.id}`);
        // setproduct(data);
        // console.log(id._id)

      // }
      // fetchProducts()
// console.log('this is id',id)
     },[dispatch,match])
     const addToCartHandler =()=>{
        navigate(`/cart/${match.params.id}?qty=${qty}`)

      }

// product={}

  return ( 
    <>
        <Link className='btn btn-dark my-3' to="/">Go back</Link>
       {loading?<Loader/>:error?<Message variant='danger'> {error}</Message>:(
        <Row>
        <Col md={6}>
          {/* {product.image} */}
          <Image src ={product.image} alt={product.name} fluid/>
        </Col>
        <Col md={3}>
           <ListGroup variant='flush'>
               <ListGroup.Item>
                   <h2>{product.name}</h2>
               </ListGroup.Item>
               <ListGroup.Item>
                   <Rating value={product.rating} text={`${product.numReviews} reviews`}/>
               </ListGroup.Item>
               <ListGroup.Item>
                Price:${product.price}
               </ListGroup.Item>
               <ListGroup.Item>
                Description:${product.description}
               </ListGroup.Item>

           </ListGroup>

        </Col>
            <Col md={3}>
              <Card>
                <ListGroup variant='flush'>
                  <ListGroupItem>
                    <Row>
                      <Col>
                        Price:
                      </Col>
                      <Col>
                      <strong>${product.price}</strong>
                      </Col>
                    </Row>
                  </ListGroupItem>

                  <ListGroup.Item>
                    <Row>
                      <Col>
                        Status:
                      </Col>
                      <Col>
                      {product.countInStock>0?'In Stock':'Out of Stock'}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                  {product.countInStock > 0 && (
                      <ListGroup.Item>
                        <Row>
                          <Col>Qty</Col>
                          <Col>
                            <Form.Control as='select' value={qty} onChange={(e)=>
                            setqty(e.target.value)}>
                              {
                              [...Array(product.countInStock).keys()].map((x)=>(
                                <option key={x+1} value={x+1}>
                                  {x+1}
                                </option>
                              ))}
                            </Form.Control>
                          </Col>
                        </Row>
                      </ListGroup.Item>
                  )}



                  <ListGroup.Item>
                    <Button className='btn-block' type='button'
                      onClick={addToCartHandler}
                      disabled={product.countInStock===0}>
                      Add to Cart
                    </Button>
                  </ListGroup.Item>
                </ListGroup>
              </Card>
            </Col>


     </Row> 
        )}

    </>

  )
}


export default Productscreens
