            ):
            <LinkContainer to='/login'>
            <Nav.Link><i className='fas fa-user'></i>Sigin In</Nav.Link>

               </LinkContainer>}
               </Nav>
        </Navbar.Collapse>

      </Container>
    </Navbar>
    </header>
  )
}

export default Header
